

def show_qa(ids):
    questions = [question.rstrip() for i, question in enumerate(open(f'../data/questions_A_prime.txt', 'r', encoding='UTF-8')) if i in ids]
    answers = [answer.rstrip() for i, answer in enumerate(open(f'../predictions/answers_A_prime_EN_h.txt', 'r', encoding='UTF-8')) if i in ids]
    with open('./check.txt','w',encoding='UTF-8') as file:
        file.write('\n'.join([f'{a}' for q,a in zip(questions,answers)]))



